const annualDiscount=20,basicPrice=19.99,profPrice=24.99,masterPrice=39.99,monthPrice=document.querySelectorAll(".price__num"),switcher=document.querySelector(".price__switcher-btn");switcher.addEventListener("click",(()=>{switcher.toggleAttribute("checked"),switcher.hasAttribute("checked")?setMonthlyPrices():setAnnualPrices()}));const setMonthlyPrices=()=>{monthPrice.forEach((t=>{t.classList.contains("basic")?t.textContent=19.99:t.classList.contains("prof")?t.textContent=24.99:t.classList.contains("master")&&(t.textContent=39.99)}))},setAnnualPrices=()=>{setMonthlyPrices(),monthPrice.forEach((t=>{const e=Math.round(12*+t.textContent*.8);t.textContent=e}))};setMonthlyPrices();